const int MOD = 1e9 + 7;

class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int arr1[1001] = {};

        for (int i = 0; i <= nums[0]; i++) {
            arr1[i] = 1;
        }

        for (int i = 1; i < nums.size(); i++) {
            int next_arr1[1001] = {};

            for (int j = 0; j <= nums[i - 1]; j++) {
                int last_arr1 = j;
                int last_arr2 = nums[i - 1] - j;


                int start = max(j, nums[i] - last_arr2);
                // int end = nums[i];

                if (start <= nums[i]) { // add (start, nums[i])
                    // next_arr1[start] += arr1[j];
                    next_arr1[start] = (next_arr1[start] +  arr1[j]) % MOD;
                }



                // for (int k = last_arr1; k <= 1001; k++) {
                //     if (k <= nums[i] && last_arr2 >= nums[i] - k) {
                //         next_arr1[k] = (next_arr1[k] + arr1[j]) % MOD;
                //     }
                // }
            }

            for (int j = 1; j <= nums[i]; j++) {
                // next_arr1[j] += next_arr1[j - 1];
                next_arr1[j] = (next_arr1[j] + next_arr1[j - 1]) % MOD;
            }

            swap(next_arr1, arr1);
        }

        int sum = 0;
        for (int i = 0; i < 1001; i++) {
            sum = (sum + arr1[i]) % MOD;
        }

        return sum;
    }
};
