func countOfPairs(nums []int) int {
    var MOD = 1000000007

    var dp = make([][]int, len(nums))
    for i := range dp {
        dp[i] = make([]int, 1005)
    }

    for i := 0; i <= nums[0]; i++ {
        dp[0][i] = 1
    }

    for i := 1; i < len(nums); i++ {
        // fmt.Println("hjehe")
        if nums[i] >= nums[i-1] {
            var diff = nums[i]-nums[i-1]
            dp[i][diff] = dp[i-1][0]
            for k := diff+1; k <= nums[i]; k++ {
                dp[i][k] += dp[i][k-1]+dp[i-1][k-diff]
                dp[i][k] %= MOD
            }

        } else {

            dp[i][0] = dp[i-1][0]
            for k := 1; k <= nums[i]; k++ {
                dp[i][k] += dp[i][k-1]+dp[i-1][k]
                dp[i][k] %= MOD
            }
        }
    }
    // fmt.Println("hjehe")
    var res = 0
    for i := 0; i <= nums[len(nums)-1]; i++ {
        res += dp[len(nums)-1][i]
        res %= MOD
    }

    return res
}
