class Solution {
public:
    vector<long long> countDown(vector<long long> &y, int x){
        long long mod = 1e9+7;
        vector<long long> ydown(x, 0);
        ydown.back() = y.back();
        for(int i = y.size()-2; i >= 0; i--){
            ydown[i] = ydown[i+1] + y[i];
            ydown[i] %= mod;
        }
        return ydown;
    }
    vector<long long> countUp(vector<long long> &y, int x){
        long long mod = 1e9+7;
        vector<long long> yup(x, 0);
        yup[0] = y[0];
        yup[0] %= mod;
        for(int i = 1; i < x; i++){
            yup[i] = yup[i-1] + y[i];
            yup[i] %= mod;
        }
        return yup;
    }
    int countOfPairs(vector<int>& nums) {
        long long mod = 1e9+7;
        int x = *max_element(nums.begin(), nums.end()) + 1;
        vector<long long> y(x, 0);
        for(int i = 0; i <= nums[0]; i++){
            y[i] = 1;
        }
        vector<long long> yup = countUp(y, x);
        vector<long long> ydown = countDown(y, x);
        //cout << yup.back() << '\n';
        for(int j = 1; j < nums.size(); j++){
            //cout << j << '\n';
            vector<long long> z(x, 0);
            for(int i = 0; i <= nums[j]; i++){
                //cout << i << '\n';
                int xx = i;
                int yy = i + nums[j-1] - nums[j];

                int k = min(xx, yy);
                if( k >= 0)
                    z[i] = yup[k];
                z[i] %= mod;
            }
            yup = countUp(z, x);
            //cout << yup.back() << '\n';
        }
        return yup.back();
    }
};
