typedef long long ll;

class Solution {
public:
    ll mod=(1e+9)+7;
    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        vector<vector<ll>> dp(n+1,vector<ll>(1002,-1));
        for(int j=0;j<=1001;j++){
            dp[n][j]=1;
        }
        vector<int> pre(1002);
        pre[0]=0;
        for(ll j=1;j<=1001;j++){
            pre[j]=(pre[j-1]+dp[n][j-1])%mod;
        }
        for(int i=n-1;i>=0;i--){
            for(ll prev=0;prev<=(i>0?nums[i-1]:0);prev++){
                ll prev2=(i>0?nums[i-1]-prev:1e+5);
                ll from=max(prev,nums[i]-prev2),to=nums[i];
                if(from<=to)
                    dp[i][prev]=((pre[to+1]-pre[from])%mod+mod)%mod;
                else
                    dp[i][prev]=0;
            }
            pre[0]=0;
            for(ll j=1;j<=1001;j++){
                pre[j]=(pre[j-1]+dp[i][j-1])%mod;
            }
        }
        return (int)dp[0][0];
    }
};
