class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        mod = 1_000_000_007 
        n = len(nums)
        dp = [[0]*1001 for _ in range(n+1)]
        dp[n][:] = [1]*1001

        for i in range(n-1, 0, -1): 
            suffix = 0 
            diff = max(0, nums[i] - nums[i-1])

            for j in range(1000, -1, -1): 
                if j + diff <= nums[i]: 
                    suffix = (suffix + dp[i+1][j+diff]) % mod
                dp[i][j] = suffix 
        return sum(dp[1][:nums[0]+1]) % mod
