class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        vector<vector<long>> v;
        v.push_back({}); v.push_back({});
        long m = 1e9+7;
        for (int i = 0; i <= nums[0]; i++){
            v[0].push_back(i+1);
        }
        for (int i = 1; i < nums.size(); i++) {
            int cur = i % 2;
            int prev = 1 - cur;
            vector<long> my;
            for (int j = 0; j <= nums[i]; j++) {
                long p = 0;
                if (j) p = my[j-1];
                int mi = min(min(j, nums[i-1]), nums[i-1]-nums[i]+j);
                long tot = 0;
                if (mi >=0) tot = v[prev][mi];
                my.push_back((tot + p)%m);
            }
            v[cur] = my;
        }
        long ans = 0;
        int k = (nums.size()-1)%2;
        // for (int i = 0; i < v[k].size(); i++) {
        //     ans = (ans + v[k][i]) % m;
        // }
        return v[k][nums[nums.size()-1]];
    }
};
