class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        const int modK = (int) 1e9 + 7;
        const int n = nums.size();
        vector<vector<int> > dp(n, vector<int>(1001, 0));
        vector<vector<int> > prefix(n, vector<int>(1001, 0));
        fill(dp[0].begin(), dp[0].begin() + nums[0] + 1, 1);
        prefix[0][0] = dp[0][0];
        for (int j = 1; j <= 1000; j++) {
            prefix[0][j] = prefix[0][j - 1] + dp[0][j];
        }
        for (int i = 1; i < n; i++) {
            for (int j = 0; j <= nums[i]; j++) {
                const int maxK = min(nums[i - 1] - nums[i] + j, j);
                if (maxK >= 0) {
                    dp[i][j] = (dp[i][j] + prefix[i - 1][maxK]) % modK;
                }
                prefix[i][j] = ((j > 0 ? prefix[i][j - 1] : 0) + dp[i][j]) % modK;
            }
            for (int j = nums[i] + 1; j <= 1000; j++) {
                prefix[i][j] = prefix[i][nums[i]];
            }
        }
        int ans = prefix[n-1][1000];
        return ans;
    }
};
