#define lln long long
class Solution {
public:
    int countOfPairs(vector<int>& a) {
        int n = a.size();
        lln mod = 1e9+7;
        lln dp[n+1][1002];
        memset(dp,0,sizeof dp);
        for(int i=0;i<n;i++){

            if(i==0){
                for(int j=0;j<=a[i];j++){
                    dp[0][j]++;

                }
            }
            else{
                for(int k = 0;k<=a[i];k++){
                    // taking cur as sp, a[i]-sp, 
                    int z = a[i-1]-a[i]+k;
                    if(z<0)continue;
                    dp[i][k] = (dp[i][k]+dp[i-1][min(z,k)])%mod;

                }

            }
            for(int j=1;j<=1000;j++){
                    dp[i][j]+=dp[i][j-1];
                    dp[i][j]%=mod;
            }


        }


        return dp[n-1][1000];
    }
};
