class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        memset(dp, 0, sizeof dp);
        memset(dp2, 0, sizeof dp2);

        const int N = nums.size();
        for (int a = 0;a <= nums[N - 1]; a++) {
            dp[a] = 1;
        }
        compute_pref_array();

        for (int i = N - 2;i >= 0;i--) {
            memset(dp2, 0, sizeof dp2);
            for (int a = 0;a <= nums[i]; a++) {
                int c_min = max(a, a + nums[i + 1] - nums[i]);
                int c_max = nums[i + 1];
                dp2[a] = pref[c_max + 1];
                dp2[a] += MOD - pref[c_min];
                dp2[a] %= MOD;
            }
            memcpy(dp, dp2, sizeof dp2);
            compute_pref_array();
        }

        return (int) pref[nums[0] + 1];
    }
private:
    static const long long MOD = 1000000007LL;
    static const int MAXN = 2048, MAXV = 1024;
    long long dp[MAXV], dp2[MAXV], pref[MAXV];

    void compute_pref_array()
    {
        pref[0] = 0LL;
        for (int i = 1;i < MAXV; i++) {
            pref[i] = (pref[i - 1] + dp[i - 1]) % MOD;
        }
    }
};
