class Solution {
public:
    using ll = long long;
    const ll MOD = 1e9 + 7;
    const int N = 1001;

    int countOfPairs(vector<int>& nums) {
        vector<ll> dp(N);
        for (int i = 0; i <= nums[0]; i++) dp[i] = 1;
        for (int i = 1; i < N; i++) dp[i] = (dp[i - 1] + dp[i]) % MOD;
        for (int i = 1; i < nums.size(); i++) {
            vector<ll> ndp(N);
            for (int j = 0; j < min(nums[i] + 1, N); j++) {
                int x = min(j, nums[i - 1] - nums[i] + j);
                if (x >= 0) ndp[j] = dp[x];
            }
            for (int j = 1; j < N; j++) ndp[j] = (ndp[j] + ndp[j - 1]) % MOD;
            swap(dp, ndp);
        }
        return dp[nums.back()];
    }
};
