class Solution {
public:
    int countOfPairs(vector<int>& nums) 
    {
    int n = nums.size();
    int ma = *max_element(nums.begin(),nums.end());
    vector<vector<int>> dp(n, vector<int>(ma+1,0));
    int mod = 1e9+7;
    for (int j = 0; j <= nums[0]; ++j) 
    {
        dp[0][j] = 1;  
    }

    for (int i = 1; i < n; ++i)
    {
        vector<int> pre(ma+1, 0);

        for (int j = 0; j <= nums[i-1]; ++j) 
        {
            pre[j] = dp[i-1][j];
            if (j > 0) 
            pre[j] = (pre[j] + pre[j-1]) % mod;
        }

        for (int j = 0; j <= nums[i]; ++j) 
        {
            int k = nums[i] - j;
            int pj = nums[i-1]-k;  // pj<= 
            pj = min(pj,j);
            if (pj>=0) 
            {
                dp[i][j] = pre[pj];
            }
        }

    }

    int ans = 0;
    for (int j = 0; j <= nums[n-1]; ++j) 
    {
        ans = (ans + dp[n-1][j]) % mod;
     //   cout<<ans<<' ';
    }

    return ans;
    }
};
