class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        const int MOD=1e9+7;
        vector<int>prev(1001,0),curr(1001,0);

        fill(prev.begin(),prev.begin()+nums[0]+1,1);

        for(int i=1;i<n;i++){
            fill(curr.begin(),curr.end(),0);
            int sum=0;
            for(int j=0;j<=nums[i];j++){
                int rem=nums[i]-j;
                int maxVal=min(j,nums[i-1]-rem);
                if(maxVal>=0){
                    sum=(sum+prev[maxVal]) % MOD;
                    curr[j]=sum;
                }
            }
            swap(prev, curr);
        }

        int res=0;
        for(int j=0;j<=nums[n-1];++j){
            res=(res+prev[j]) % MOD;
        }
        return res;
    }
};
