class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int m = 1e9 + 7;
        vector<vector<int> >table (2, vector<int>(1001, 0));
        for(int i = 0; i <= nums[0]; i++) table[0][i] = i+1;
        for(int i = nums[0] + 1; i < 1001; i++) table[0][i] = nums[0]+1;
        int x, y, k;
        int c = 1;
        int l;
        for(int i = 1; i < nums.size(); i++){
            x = nums[i - 1];
            y = nums[i];
            l = 0;
            for(int j = 0; j <= y; j++){
                table[c][j] = l;
                k = min(j, x);
                k = min(x - y + j, k);
                if(k >= 0){
                    table[c][j] = (table[c][j] + table[c^1][k])%m;
                }
                l = table[c][j];
            }
            for(int j = y + 1; j < 1001; j++) table[c][j] = l;
            c ^= 1;
        }
        c ^= 1;
        return table[c].back();      
    }
};
