class Solution {
public:
    typedef long long ll;
    int MOD = 1e9 + 7;
    int N = 1000;


    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<ll>> dp(n, vector<ll>(N+1, 0));

        dp[0][0] = 1;
        for (int x = 1; x <= nums[0]; x++) {
            dp[0][x] = dp[0][x-1] + 1;
        }

        for (int i = 1; i < n; i++) {
            int z = nums[i];
            int pz = nums[i-1];

            int d = z - pz;

            if (d <= 0) {
                for (int x = 0; x <= z; x++) {
                    dp[i][x] += dp[i-1][x];
                    dp[i][x] %= MOD;
                }
            } else {
                for (int x = d; x <= z; x++) {
                    dp[i][x] += dp[i-1][x-d];
                    dp[i][x] %= MOD;
                }
            }

            for (int x = 1; x <= z; x++) {
                dp[i][x] += dp[i][x-1];
                dp[i][x] %= MOD;
            }
        }

        // dp[x][y] = pdp[x]

//         cout << endl;
//         for (int i = 0; i < n; i++) {
//             for (int j = 0; j <= nums[i]; j++) {
//                 cout << dp[i][j] << " ";
//             }
//             cout << endl;
//         }

        ll res = dp[n-1][nums[n-1]];
        // for (int x = 0; x <= nums[n-1]; x++) {
        //     res += dp[n-1][x];
        //     res %= MOD;
        // }

        return res;
    }
};
