class Solution {
public:
    const int MOD = 1e9 + 7;

    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        int maxVal = *max_element(nums.begin(), nums.end());

        // dp[i][j] will store the number of valid pairs for the first i+1 elements where arr1[i] = j.
        vector<vector<int>> dp(n, vector<int>(maxVal + 1, 0));

        // Initialize dp for the first element.
        for (int j = 0; j <= nums[0]; ++j)
            dp[0][j] = 1; // There's one way to make arr1[0] = j and arr2[0] = nums[0] - j.

        // Fill the dp array using prefix sums.
        for (int i = 1; i < n; ++i) {
            vector<int> prefixSum(maxVal + 2, 0);

            // Compute prefix sums for dp[i-1].
            for (int k = 0; k <= maxVal; ++k)
                prefixSum[k + 1] = (prefixSum[k] + dp[i - 1][k]) % MOD;

            for (int j = 0; j <= nums[i]; ++j) {
                int arr2_val = nums[i] - j;
                int maxK = min(j, nums[i - 1] - arr2_val);

                if (maxK >= 0)
                    dp[i][j] = prefixSum[maxK + 1];
            }
        }

        // Sum up the results for the last element.
        int result = 0;

        for (int j = 0; j <= nums[n-1]; ++j)
            result = (result + dp[n-1][j]) % MOD;

        return result;
    }
};
