class Solution {
    int dp[][][];
    int mod = (int)1e9+7;
    public int countOfPairs(int[] nums) {
        int max = nums[0];
        for(int i : nums){
            max = Math.max(max , i);
        }
        long ans = 0;
        this.dp = new int[nums.length][max+1][2];
        for(int a[][] : dp)
            for(int b[] : a)
                Arrays.fill(b,-1);
        for(int nf = 0 ; nf <= nums[0] ; nf++){
            int ns = nums[0]-nf;
            ans += rec(nums,1,nf,ns,0);
            ans %= mod;
        }
        return (int)ans;
    }
    private int rec(int[] nums , int i , int f , int s , int c){
        if(i == nums.length)return 1;
        if(dp[i][f][c]!=-1)return dp[i][f][c];
        long ans = 0;
        if(c==0){
            int nf = f;
            int ns = nums[i]-nf;
            if(s<ns){
                nf += ns-s;
                ns = s;
            }
            if(nf<=nums[i]&&ns>=0){
                ans += rec(nums,i,nf,ns,1);
                ans %= mod;
            }
        }else{
            int nf = f+1;
            int ns = nums[i]-nf;
            if(nf<=nums[i]&&ns>=0){
                ans += rec(nums,i,nf,ns,1);
                ans %= mod;
            }
            ans += rec(nums,i+1,f,s,0);
            ans %= mod;
        }

        return dp[i][f][c] = (int)ans;
    }
}
