class Solution {
public:
    using ll = long long;
    const ll mod = 1e9 + 7;
    const int m = 1e3 + 2;
    int countOfPairs(vector<int>& a) {
        int n = a.size();
        vector<vector<ll> > dp(n + 2, vector<ll> (m + 2, 0));
        for(int i = 0; i <= m; i++) {
            dp[0][i] = (i <= a[0]);
            if(i) dp[0][i] += dp[0][i - 1];
        }

        for(int i = 1; i < n; i++) {
            for(int j = 0; j <= a[i]; j++) {
                int y = a[i] - j;
                int r = min(j, a[i - 1] - y);
                if(r >= 0) dp[i][j] = dp[i - 1][r];
                if(j) dp[i][j] = (dp[i][j] + dp[i][j - 1]) % mod;
            }

        }
        ll res = dp[n - 1][a[n - 1]];

        return res;
    }
};
