class Solution {
public:
    int countOfPairs(vector<int>& nums) {

        int n = nums.size();

        long long mod = 1000000007;

        vector<vector<long long>> memo(n, vector<long long>(1001, 0));

        memo[0][0] = 1;

        for (int i = 0; i < n-1; ++i) {
            long long v = 0;
            // cout<<i<<endl;
            for (int j = 0; j <= nums[i]; ++j) {
                v += memo[i][j];
                v %= mod;
                if (v > 0) {
                    if (nums[i+1] > nums[i]) {
                        memo[i+1][nums[i+1] - (nums[i] - j)] += v;
                        memo[i+1][nums[i+1] - (nums[i] - j)] %= mod;
                    } else {   
                        memo[i+1][j] += v;
                        memo[i+1][j] %= mod;
                    }
                }
                // cout<<v<<" ";
            }
            // cout<<endl;
        }

        long long sol = 0;

        long long vv = 0;

        for (int i = 0; i <= nums[n-1]; ++i) {
            vv += memo[n-1][i];
            vv %= mod;
            sol += vv;
            sol %= mod;
        }

        return sol;

    }
};
