class Solution {
    vector<vector<int>> dp;
    const int mod = 1e9 + 7;

public:
    int countOfPairs(vector<int>& a) {
        int n = a.size();
        int i, j;
        dp = vector<vector<int>>(n, vector<int>(1005, 0));
        vector<vector<int>> v(n, vector<int>(2));
        for (int i = 0; i < n; i++)
            v[i] = {a[i] - 1000, a[i]};
        v[0][0] = max(v[0][0], 0);
        for (i = 1; i < n; i++)
            v[i][0] = max(v[i][0], v[i - 1][0]);
        for (j = v[n - 1][1]; j >= v[n - 1][0]; j--)
            dp[n - 1][j] = dp[n - 1][j + 1] + 1;
        for (i = n - 2; i >= 0; i--) {
            for (j = v[i][1]; j >= v[i][0]; j--)
                dp[i][j] = (dp[i][j + 1] + dp[i + 1][j+(a[i+1]>a[i]?a[i+1]-a[i]:0)]) % mod;
        }
        return dp[0][v[0][0]];
    }
};
