class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        mod = 10 ** 9 + 7
        n = len(nums)
        dp = [[0] * (max(nums)+1) for _ in range(n)]

        for x in range(nums[0]+1):
            dp[0][x] = 1

        for i in range(1, n):
            for x in range(nums[i]+1):
                total = 0
                y = min(x, nums[i-1] - nums[i] + x)
                if y >= 0:
                    dp[i][x] = dp[i-1][y]
                if x > 0:
                    dp[i][x] = (dp[i][x] + dp[i][x-1]) % mod

        res = 0
        for val in dp[n-1]:
            res = (res + val) % mod

        return res
