from sortedcontainers import SortedList
class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        mod = int(1e9 + 7)
        mx = 50
        n = len(nums)
        dp = [ [0 for i in range(nums[j] + 1)] for j in range(n) ]
        for i in range(nums[0] + 1):
            dp[0][i] = 1
        for i in range(1, n):
            j = 0
            cur = 0
            for k in range(nums[i] + 1):
                while j <= nums[i-1] and j <= k and nums[i-1] - j >= nums[i] - k:
                    cur = (cur + dp[i-1][j]) % mod
                    j += 1
                dp[i][k] = (dp[i][k] + cur) % mod
        ans = 0
        for x in dp[-1]:
            ans = (ans + x) % mod
        return ans
