class Solution {
    public int countOfPairs(int[] nums) {
        int n = nums.length;
        long prev[]=new long[1001];
        for(int i=0;i<=nums[0];i++) prev[i]=1;
        int mod = (int)1e9+7;
        for(int i = 1;i<n;i++){
            for(int j=1;j<=1000;j++) prev[j]=(prev[j]+prev[j-1])%mod;
            long dp[]=new long[1001];
            for(int j = 0;j<=nums[i];j++){
                int val = Math.min(j,nums[i-1]-nums[i]+j);
                if(val<0) continue;
                dp[j]=(dp[j]+prev[val])%mod;
            }
            prev = dp;
        }


        long ans = 0;
        for(int i=0;i<=nums[n-1];i++) ans = (ans+prev[i])%mod;
        return (int)ans;
    }
}
