class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        const int SIZE = nums.size(), MOD = 1e9 + 7;
        vector<vector<int>> dp(SIZE,vector<int>());
        vector<int> acc(1001, 1);
        int res = 0;

        for(int i = 0;i < SIZE;i++)
            dp[i] = vector<int>(nums[i] + 1);

        for(int i = 0;i < dp[0].size();i++)
            dp[0][i] = 1;

        for(int i = 1;i <= 1000;i++)
            acc[i]+= acc[i - 1];

        for(int i = 1;i < SIZE;i++) {
            for(int j = 0;j <= nums[i];j++) {
                int max1 = j, max2 = nums[i - 1] - (nums[i] - j);
                int target = min(max1, max2);
                if(target < 0)
                    continue;
                dp[i][j] = acc[target];
            }
            acc = vector<int>(1001,0);
            acc[0] = dp[i][0];
            for(int j = 1;j <= nums[i];j++)
                acc[j] = (dp[i][j] + acc[j - 1]) % MOD;
        }

        for(int i = 0;i <= nums.back();i++)
            res = (res + dp.back()[i]) % MOD;

        return res;
    }
};
