#define MOD 1000000007
class Solution {
public:
    int countOfPairs(vector<int>& a) {
        int n = a.size();
        int dp[2001][1001];
        memset(dp, 0, sizeof(dp));

        int ma = 0;
        for (auto &x: a) ma = max(ma, x);

        for (int j = 0; j <= a[0]; j++) {
            dp[0][j] = 1;
        }
        for (int j = 1; j <= ma; j++) {
            dp[0][j] += dp[0][j-1];
        }

        for (int i = 1; i < n; i++) {
            for (int j = 0; j <= a[i]; j++) {
                int diff = a[i] - a[i-1];
                int k = a[i] - j;
                if (diff == 0) dp[i][j] = dp[i-1][j];
                else if (diff > 0) {
                    if (j - diff >= 0) dp[i][j] = dp[i-1][j-diff];
                }
                else { // diff < 0
                    dp[i][j] = dp[i-1][j];
                    //if (a[i-1]-k >= 0) dp[i][j] = dp[i-1][a[i-1]-k];
                }
            }
            for (int j = 1; j <= ma; j++) {
                dp[i][j] += dp[i][j-1];
                dp[i][j] %= MOD;
            }
        }
        /*
        for (int i = 0; i < n; i++) {
            for (int j = 0; j <= ma; j++) {
                printf("%d ", dp[i][j]);
            }
            cout << endl;
        }*/
        return dp[n-1][ma];
    }
};
