class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        const int mod = 1e9 + 7;
        const int MAX_SUM = 1000;

        vector<vector<long long>> dp(n,vector<long long> (MAX_SUM+1));

        for (int j = 0; j <= nums[0]; ++j){
            dp[0][j] = 1;
        }


        for(int i=1;i<n;i++){
            vector<long long> pre(MAX_SUM+1);
            pre[0]=dp[i-1][0];
            for(int j=1;j<=nums[i-1];j++){
                pre[j]+=(pre[j-1]+dp[i-1][j]);
                pre[j]%=mod;
            }
            for(int j=0;j<=nums[i];j++){
                // for(int k=0;k<=min(nums[i-1],j);k++){
                //     if(nums[i]-j<=nums[i-1]-k){
                //         dp[i][j]+=dp[i-1][k];
                //         dp[i][j]%=mod;
                //     }
                // }
                int ind = min(j+nums[i-1]-nums[i],j);
                if(ind>=0){
                    dp[i][j]+=pre[ind];
                    dp[i][j]%=mod;   
                }
            }
        }

        long long ans = 0;
        for(int i=0;i<=nums[n-1];i++){
            ans+=dp[n-1][i];
            ans%=mod;
        }

        return ans;
    }
};
