class Solution(object):
    MOD = 10**9 + 7

    def __init__(self):
        self.memo = []
        self.elements = []

    def find_pairs(self, index, previous_value):
        if previous_value > self.elements[index]:
            return 0
        if index + 1 == len(self.elements):
            return self.elements[index] - previous_value + 1
        if self.memo[index][previous_value] != -1:
            return self.memo[index][previous_value]

        count = 0
        count = self.find_pairs(index, previous_value + 1)
        next_value = self.elements[index + 1] - self.elements[index] + previous_value
        next_value = max(previous_value, next_value)
        count += self.find_pairs(index + 1, next_value)
        count %= self.MOD

        self.memo[index][previous_value] = count
        return count

    def countOfPairs(self, nums):
        self.elements = nums
        n = len(self.elements)
        self.memo = [[-1] * 1001 for _ in range(n)]

        return self.find_pairs(0, 0)
