class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        MOD = 10**9 + 7
        n = len(nums)
        mx = max(nums)
        dp = [[0] * (mx + 1) for _ in range(n)]

        for j in range(nums[0] + 1):
            dp[0][j] = 1

        for i in range(1, n):
            presum = [0] * (mx + 2)
            for j2 in range(nums[i - 1] + 1):
                presum[j2 + 1] = (presum[j2] + dp[i - 1][j2]) % MOD

            for j in range(0, nums[i] + 1):
                max_j2 = min(j, nums[i - 1], nums[i - 1] - nums[i] + j)
                if max_j2 >= 0:
                    dp[i][j] = presum[max_j2 + 1] % MOD

        return sum(dp[n - 1]) % MOD

