class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        const int MOD = 1e9 + 7;
        const int max_num = 1000;
        const int min_num = 0;
        int n = nums.size();

        vector<vector<int>> dp(n + 1, vector<int>(max_num + 1, 0));

        for (int x = 0; x <= max_num; ++x) {
            dp[n][x] = 1;
        }

        for (int i = n - 1; i >= 0; --i) {
            vector<int> prefix_sum(max_num + 2, 0);
            for (int x = max_num; x >= 0; --x) {
                prefix_sum[x] = (prefix_sum[x + 1] + dp[i + 1][x]) % MOD;
            }

            for (int prev1_lb = max_num; prev1_lb >= 0; --prev1_lb) {
                int prev2_up = (i > 0) ? nums[i - 1] - prev1_lb : max_num;

                int X = nums[i] - prev2_up;
                int lb = max({X, 0, prev1_lb});

                if (lb <= nums[i]) {
                    dp[i][prev1_lb] = (prefix_sum[lb] - prefix_sum[nums[i] + 1] + MOD) % MOD;
                    dp[i][prev1_lb] = max(dp[i][prev1_lb], 0);
                }
            }
        }

        return dp[0][min_num];
    }
};
