const int M=(int)1e9+7;
class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        vector<long long> dp1(1001);
        for(int i=0;i<=nums[0];++i){
            dp1[i]=1;   
        }
        for(int i=0;i<=1000;++i){
            if(i)dp1[i]+=dp1[i-1];
            if(dp1[i]>=M)dp1[i]-=M;
        }
        for(int i=1;i<n;++i){
            vector<long long> dp2(1001);
            int diff=nums[i]-nums[i-1];
            for(int j=0;j<=1000;++j){
                if(nums[i]<j)break;
                if(diff<=0){
                    dp2[j]+=dp1[j];
                    if(dp2[j]>=M)dp2[j]-=M;
                }else if(j>=diff){
                    dp2[j]+=dp1[j-diff];
                    if(dp2[j]>=M)dp2[j]-=M;
                }
            }
            for(int j=0;j<=1000;++j){
                if(j)dp2[j]+=dp2[j-1];   
                if(dp2[j]>=M)dp2[j]-=M;
            }
            dp1=dp2;
        }
        int res=dp1[1000];
        return res;
    }
};
