class Solution {
public:
    int M = 1e9+7;
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        int dp[2001][1001] = {0};
        int sum[2001][1001] = {0};

        dp[0][0] = 1;
        sum[0][0] = 1;
        for(int j = 1; j <= nums[0] ;j ++)
        {
            dp[0][j] = 1;
            sum[0][j] = sum[0][j-1] + 1;
        }

        for(int i = 1; i < n; i++ )
        {
            if(nums[i-1] >= nums[i]) dp[i][0] = sum[i-1][0];
            sum[i][0] = dp[i][0];
            for(int j = 1; j <= nums[i]; j++)
            {
                if(j+ nums[i-1] - nums[i] >= 0) dp[i][j] = sum[i-1][min(j, j+ nums[i-1] - nums[i])];
                sum[i][j] = sum[i][j-1] + dp[i][j];
                if (sum[i][j] >= M) sum[i][j] -= M;
            }
        }

        return sum[n-1][nums[n-1]];
    }
};
