class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        int mod = 1e9 + 7;
        vector<vector<int>> dp(n, vector<int>(1001, 0));

        // init dp[n-1]
        for (int x = 0; x <= nums[n - 1]; x++) {
            dp[n - 1][x] = 1;
            if (x > 0) {
                dp[n - 1][x] += dp[n - 1][x - 1];
            }
        }

        for (int i = n - 2; i >= 0; i--) {
            // set arr1[i] = x
            for (int x = 0; x <= nums[i]; x++) {
                // arr1[i + 1]
                int y = nums[i + 1] - nums[i] + x;
                y = max(x, y);

                // range sum dp[i+1][y] - dp[i+1][nums[i+1]];
                if (y <= nums[i + 1]) {
                    // in case y = 0
                    int subtract = y == 0 ? 0 : dp[i + 1][y - 1];
                    dp[i][x] = (dp[i + 1][nums[i + 1]] - subtract + mod) % mod;
                }

                if (x > 0) {
                    dp[i][x] = (dp[i][x] + dp[i][x - 1]) % mod;
                }
            }
        }

        return dp[0][nums[0]];
    }
};
