class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        mod = 10**9 + 7
        N = len(nums)
        mx = max(nums)
        dp = [[0] * (mx + 1) for _ in range(N + 1)]

        for last1 in range(mx + 1):
            dp[N][last1] = 1

        for i in range(N - 1, -1, -1):
            prefix = [0]
            for j in range(mx + 1):
                prefix.append((prefix[-1] + dp[i + 1][j]) % mod)

            for last1 in range(mx + 1):
                last2 = float('inf') if i == 0 else nums[i - 1] - last1
                l = max(last1, nums[i] - last2)
                r = nums[i]
                if l<=r:
                    dp[i][last1] = (prefix[min(r + 1, mx + 1)] - prefix[l]) % mod

        return dp[0][0]
