class Solution {
public:
    int countOfPairs(vector<int>& nums) {
      long long mod=1e9+7;
      int n=nums.size();
      int max_n=1010;
      int dp[n][max_n];
      int per[max_n];
      for (int i=0; i<n; i++)
      for (int j=0; j<max_n; j++)
      dp[i][j]=0;
      for (int j=0; j<=nums[0]; j++){
          dp[0][j]=1;
        }
        long long tmp;
      for (int i=1; i<n; i++){
        per[0]=dp[i-1][0];
        for (int j=1; j<max_n; j++){
          per[j]=(per[j-1]+dp[i-1][j])%mod;
        }
        if (nums[i]<=nums[i-1]){
        for (int j=0; j<=nums[i]; j++){
         dp[i][j]=per[j];   
        }
        }else{
        for (int j=nums[i]-nums[i-1]; j<=nums[i]; j++){
         dp[i][j]=per[j-(nums[i]-nums[i-1])];   
        } 
        }
      }
      int ans=0;
      for (int i=0; i<=nums[n-1]; i++){
         ans=(ans+dp[n-1][i])%mod;
      }
      return ans;
    }
};
