#define MOD 1000000007

class Solution {
private:
    int A[2000][1001];
    int S[2000][1001];
public:
    int countOfPairs(vector<int>& nums) {
        int n = (int)nums.size();
        for(int i=0;i<n;i++){
            fill(A[i],A[i]+1001,0);
        }

        for(int i=0;i<=nums[0];i++){
            A[0][i] = 1;
        }

        S[0][0] = A[0][0];
        for(int j=1;j<=1000;j++){
            S[0][j] = S[0][j-1] + A[0][j];
            S[0][j] %= MOD;
        }

        for(int i=1;i<n;i++){
            for(int j=0;j<=nums[i];j++){
                int j1 = nums[i] - j;
                int m = min(min(j,nums[i-1]),nums[i-1]-j1);
                if(m >= 0){
                    A[i][j] = S[i-1][m];
                }
            }

            S[i][0] = A[i][0];
            for(int j=1;j<=1000;j++){
                S[i][j] = S[i][j-1] + A[i][j];
                S[i][j] %= MOD;
            }
        }

        int ret = 0;
        for(int i=0;i<=nums[n-1];i++){
            ret += A[n-1][i];
            ret %= MOD;
        }

        return ret;
    }
};
