class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        vector<int> dp(1001);
        for(int i = 0; i <= nums[0]; i++) dp[i] = 1;
        int MOD = 1e9+7;

        for(int i = 1; i < nums.size(); i++){
            vector<int> prefix(1002);
            for(int i = 1; i <= 1001; i++) prefix[i] = (dp[i-1]+prefix[i-1])%MOD;

            fill(dp.begin(), dp.end(), 0);
            for(int cur = 0; cur <= nums[i]; cur++){
                int upper_bound = min(nums[i-1] - nums[i] + cur, cur);
                // cout<<upper_bound<<endl;
                if(upper_bound < 0) continue;

                dp[cur] = prefix[upper_bound+1];
            }
            // cout<<endl;
        }

        int ans = 0;
        for(auto num:dp){
            ans += num;
            ans %= MOD;
        }
        return ans;
    }
};
