    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        long long ans = 0;
        vector<vector<int>> dp(n, vector<int>(1002, 0));
        vector<vector<int>> pre(n, vector<int>(1002, 0));
        for(int i = 0;i<n;i++){
            for(int j = 0;j<=nums[i];j++){
                if(i==0){
                    dp[i][j] = 1;
                    if(j==0) pre[i][j] = dp[i][j];
                    else pre[i][j] = (pre[i][j-1]+dp[i][j])%mod;
                }
                else{
                    int t = min(j, nums[i-1]-(nums[i]-j));
                    if(t>=0){
                        dp[i][j] = pre[i-1][t];
                        if(j>0) pre[i][j] = (dp[i][j]+pre[i][j-1])%mod;
                        else pre[i][j] = dp[i][j];
                    }
                    else if(j>0) pre[i][j] = pre[i][j-1];
                }
                // cout<<dp[i][j]<<" ";
            }
            // cout<<endl;
        }

        return pre[n-1][nums[n-1]];

    }
