class Solution {
public:
	int countOfPairs(vector<int>& nums) {

		int n = si( nums ) ;

		vector < array< int , 3 >  > pre ; pre.pb({0, 1002, 1});


		for ( int i = 0 ; i < n ; i++ )
		{
			int val = nums[i] ;

			vector<array<int, 3> > dp ;

			int j = 0 , m = si(pre) ;
			int currsum = 0 ;

			for ( int k = 0 ; k <= val ; k++ )
			{
				int rem = val - k ;

				while ( j < m and pre[j][0] <= k and pre[j][1] >= rem )
				{
					currsum = ( 0LL + currsum + pre[j][2] ) % mod ;
					j++;
				}
				dp.push_back({ k , rem , currsum } ) ;
			}
			pre = dp ;
		}
		int res = 0 ;

		for ( auto & i : pre )
		{
			res = (0LL + res + i[2] ) % mod;
		}
		return res ;

	}
};
