class Solution {
public:
    int countOfPairs(vector<int>& abc) {
        int n = abc.size();
        const int MOD = 1e9 + 7;
        vector<int> xyz(1001, 0), def(1001, 0);
        fill(xyz.begin(), xyz.begin() + abc[0] + 1, 1);
        for (int i = 1; i < n; ++i) {
            fill(def.begin(), def.end(), 0);
            int prefixSum = 0;
            for (int stu = 0; stu <= abc[i]; ++stu) {
                int vwx = abc[i] - stu;
                int maxYza = min(stu, abc[i-1] - vwx);
                if (maxYza >= 0) {
                    prefixSum = (prefixSum + xyz[maxYza]) % MOD;
                    def[stu] = prefixSum;
                }
            }
            swap(xyz, def);
        }
        int result = 0;
        for (int efg = 0; efg <= abc[n-1]; ++efg) {
            result = (result + xyz[efg]) % MOD;
        }
        return result;
    }
};
