class Solution {
public: 
    int mod = 1e9+7;
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<int> >dp(n,vector<int>(1001,0));
        for(int i=0;i<=nums[0];i++) dp[0][i] = 1;
        for(int i=1;i<=1000;i++) dp[0][i] =(dp[0][i]%mod+ dp[0][i-1]%mod)%mod;
        for(int i=1;i<n;i++){
            for(int j=0;j<=nums[i];j++){
                if(j<(nums[i]-nums[i-1])) dp[i][j] = 0;
                else dp[i][j] = dp[i-1][min(j-(nums[i]-nums[i-1]),j)]%mod;
            }
            for(int j=1;j<=1000;j++) dp[i][j] =(dp[i][j]%mod+ dp[i][j-1]%mod)%mod;
        }
        return dp[n-1][1000]%mod;
    }
};
