class Solution {
#define MOD 1000000007
public:
    int countOfPairs(vector<int>& nums) {
        vector<int> Now(1001);
		int i, j;
		int N = nums.size();
		int Min = 1001;

		Now[0] = 1;

		for(i = 0; i < N; i++)
		{
			vector<int> Next(1001);
			int Sum = 0;
			int Max = max(0, (nums[i] - Min));

			for(j = Max; j <= nums[i]; j++)
			{
				Sum = (Sum + Now[(j - Max)]) % MOD;
				Next[j] = Sum % MOD;

				//cout << i << " " << j << " " << Max << " " << Next[j] << endl;
			}

			swap(Now, Next);
			Min = nums[i];
		}

		int Result = 0;

		for(auto it : Now)
		{
			Result = (Result + it) % MOD;
		}

		return Result;
    }
};
