#include <bits/stdc++.h>

using namespace std;
using nagai = long long;


const int mod=1e9+7;
class Solution {
public:
    int countOfPairs(const vector<int>& nums) {
        int n = nums.size();
        int mx = *max_element(nums.begin(), nums.end());
        vector<vector<int>>dp(n, vector<int>(mx + 1));
        vector<vector<int>>pref(n, vector<int>(mx + 2));
        for (int ft = 0; ft <= nums[0]; ++ft) {
            dp[0][ft] = 1;
            pref[0][ft + 1] = pref[0][ft] + dp[0][ft];
        }
        for (int i = 1; i < n; ++i) {
            for (int val = 0; val <= nums[i]; ++val) {
                int r = val; //incl
                int val1 = nums[i] - val;
                int rr = nums[i - 1] - val1;
                r = min(r, rr);
                if (r >= 0)
                  dp[i][val] = pref[i - 1][r + 1];
            }
            for (int val = 0; val <= mx; ++val) {
                pref[i][val + 1] = pref[i][val] + dp[i][val];
                pref[i][val + 1] %= mod;
            }
        }
        return pref[n - 1][mx + 1];
    }
};
