class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        MOD = 10**9 + 7
        n = len(nums)

        dp = [0] * 1001
        prefix_sum = [0] * 1002

        for j in range(nums[0] + 1):
            dp[j] = 1
            prefix_sum[j+1] = (prefix_sum[j] + dp[j]) % MOD

        for i in range(1, n):
            new_dp = [0] * 1001
            new_prefix_sum = [0] * 1002

            for j in range(nums[i] + 1):
                left = 0
                right = min(j, nums[i-1] - (nums[i] - j))
                if left <= right:
                    new_dp[j] = (prefix_sum[right+1] - prefix_sum[left] + MOD) % MOD

            for j in range(1001):
                new_prefix_sum[j+1] = (new_prefix_sum[j] + new_dp[j]) % MOD

            dp = new_dp
            prefix_sum = new_prefix_sum

        return sum(dp) % MOD
