#include <algorithm>
class Solution {
public:
    typedef long long ll;
    int countOfPairs(vector<int>& nums) {
        ll n=nums.size();
        vector <vector<ll>> dp(n+1,vector<ll>(1001));
        ll mod=1e9+7;
        for(ll i=0;i<=n;i++)
        {
            for(ll j=0;j<=1000;j++)
            {
                dp[i][j]=0;
            }
        }
        vector <ll> pr(1001);
        for(ll i=0;i<n;i++)
        {
            for(ll j=0;j<=1000;j++)
            {
                if(i==0)
                {
                    if(j<=nums[i])
                    {
                        dp[i][j]=1;
                    }

                }
                else
                {
                   ll mm=j;
                    if(j+nums[i-1]-nums[i]<j)
                        mm=j+nums[i-1]-nums[i];
                    if(nums[i-1]<mm)
                        mm=nums[i-1];
                    // cout<<mm<<endl;
                    if(nums[i]-j>=0&&mm>=0)
                    dp[i][j]=pr[mm]; 
                }
            }
            for(ll j=0;j<=1000;j++)
            {
                if(j==0)
                {
                    pr[j]=dp[i][j];
                }
                else
                {
                    pr[j]=(pr[j-1]+dp[i][j])%mod;
                }
            }
        }
        ll ans=0;
        for(ll j=0;j<=1000;j++)
        {
            ans=(ans+dp[n-1][j])%mod;
        }
        int ret=ans;
        return ret;
    }
};
