class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        int mod = 1e9 + 7;

        int dp[n + 1][1002];
        memset(dp, 0, sizeof(dp));

        for(int i = 1000; i >= 0; i--)
            dp[n][i] = dp[n][i + 1] + 1;; 

        for(int i = n - 1; i > 0; i--) 
            for(int j = nums[i - 1]; j >= 0; j--) {
                int lowerBound = max(j, nums[i] - nums[i - 1] + j);
                if(lowerBound < 0)
                    dp[i][j] = (dp[i][j + 1] + (dp[i + 1][0] - dp[i + 1][nums[i] + 1] + mod) % mod) % mod;
                else if(lowerBound <= nums[i] + 1)
                    dp[i][j] = (dp[i][j + 1] + (dp[i + 1][lowerBound] - dp[i + 1][nums[i] + 1] + mod) % mod ) % mod;
            }

        return (dp[1][0] - dp[1][nums[0] + 1] + mod) % mod;
    }
};
/*
j <= k <= nums[i]
nums[i] - k <= nums[i - 1] - j
k >= nums[i] - nums[i - 1] + j;
*/
