using ll = long long;

class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int mod = 1e9 + 7;
        int n = nums.size();
        vector<ll> dp(1001);
        vector<ll> dpp(1001);
        for (int i = 0; i <= nums[0]; i++) {
            dp[i] = 1;
        }
        for (int i = 0; i < 1001; i++) {
            dpp[i] = (dp[i] + (i > 0 ? dpp[i - 1] : 0)) % mod;
        }
        for (int i = 1; i < n; i++) {
            int v = nums[i];
            vector<ll> dp2(1001);
            // cout << "--- " << i << endl;
            for (int j = 0; j <= v; j++) {
                int k = min(j, nums[i - 1] - (nums[i] - j));
                if (k < 0) {
                    continue;
                }
                // cout << j << " " << k << endl;
                dp2[j] = (dp2[j] + dpp[k]) % mod;
                // cout << " j = " << j << " dp = " << dp[j] << endl;
            }

            dp = dp2;
            for (int i = 0; i < 1001; i++) {
                dpp[i] = (dp[i] + (i > 0 ? dpp[i - 1] : 0)) % mod;
            }
        }
        ll res = 0;
        for (int i = 0; i < 1001; i++) {
            res = (res + dp[i]) % mod;
        }
        return res;
    }
};
