class Solution {
public:
#define ll long long
    int countOfPairs(vector<int>& nums) {
        ll n = nums.size();
        ll mod = 1e9+7;
        vector<vector<ll>> dp(n,vector<ll>(1001,0LL));
        vector<ll> pre(1001,0LL);
        for(int i=0;i<=nums[0];i++){
            dp[0][i]=1LL;
        }
        for(int i=0;i<=1000;i++){
            pre[i]=dp[0][i];
            if(i>0)pre[i]+=pre[i-1];
        }
        for(ll i=1;i<n;i++){
            vector<ll> temp(1001,0);
            for(ll j=0;j<=nums[i];j++){
                ll x = min(j-(ll)nums[i]+(ll)nums[i-1],j);
                if(x<0LL)continue;
                dp[i][j] = pre[x];
                temp[j] = dp[i][j];
            }
            for(int k=0;k<=1000;k++){
                pre[k]=temp[k];
                if(k>0){
                    pre[k]=(pre[k]+pre[k-1])%mod;
                }
            }
        }
        ll ans = 0;
        for(int i=0;i<=1000;i++){
            ans = (ans+dp[n-1][i])%mod;
        }
        return ans;
    }
};
