class Solution(object):
    def countOfPairs(self, nums):
        """
        :type nums: List[int]
        :rtype: int
        """
        arr1_pre_dp = []
        mod = 10**9 + 7
        for i in range(nums[0]+1):
            arr1_pre_dp.append(1)

        for i in range(1, len(nums)):
            arr1_new_dp = [0 for _ in range(nums[i]+1)]
            if nums[i-1] >= nums[i]:
                arr1_new_dp[0] = arr1_pre_dp[0]
                for j in range(1, nums[i]+1):
                    arr1_new_dp[j] = arr1_new_dp[j-1] + arr1_pre_dp[j]
                    arr1_new_dp[j] = arr1_new_dp[j] % mod
            else:
                j_min = nums[i] - nums[i-1]
                arr1_new_dp[j_min] = arr1_pre_dp[0]
                for j in range(j_min+1, nums[i]+1):
                    arr1_new_dp[j] = arr1_new_dp[j-1] + arr1_pre_dp[j-j_min]
                    arr1_new_dp[j] = arr1_new_dp[j] % mod

            del arr1_pre_dp
            arr1_pre_dp = arr1_new_dp

        return sum(arr1_pre_dp) % mod
