class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        const long long MOD = 1e9 + 7;
        vector<vector<long long>> dp(nums.size(), vector<long long>(1050));
        dp[0][0] = 1;
        dp[0][nums[0]+1] = -1;
        for (long long i = 0; i+1 < nums.size(); i++) {
            long long curr = 0;
            for (long long j = 0; j <= nums[i]; j++) {
                curr = (curr + dp[i][j]) % MOD;
                long long low = max(j, nums[i+1] - (nums[i] - j));
                long long high = nums[i+1];
                if (low <= high) {
                    dp[i+1][low] = (dp[i+1][low] + curr) % MOD;
                    dp[i+1][high+1] = (dp[i+1][high+1] - curr) % MOD;
                }
            }
        }
        long long ans = 0, curr = 0;
        for (long long cnt : dp.back()) {
            curr = (curr + cnt) % MOD;
            ans = (ans + curr) % MOD;
        }
        return ans;
    }
};
