MOD = 10**9+7
class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        n = len(nums)
        m = max(nums)+2
        pre = [i for i in range(m)]

        for i in reversed(range(n)):
            curr = [0] * m
            for a in range(m-1):
                if i > 0:
                    j = max(a, nums[i] - nums[i-1] + a)
                else:
                    j = a

                if j > nums[i]:
                    curr[a+1] = curr[a] 
                else:
                    curr[a+1] = (curr[a] + pre[nums[i]+1] - pre[j]) % MOD               
            pre = curr
        # print('---'*n)
        # print(pre)
        return pre[1]
