class Solution {
public:
    int countOfPairs(vector<int>& nums) {

        auto MOD = 1000000000 + 7;

        auto n = nums.size();

        auto d = vector<vector<int>>(n, vector<int>(1001, 0));
        auto s = vector<vector<int>>(n, vector<int>(1001, 0));

        for (auto i = 0; i < n; ++ i)
        {
            for (auto j = 0; j < 1001; ++j)
            {
                if (i == 0)
                {
                    d[i][j] = 1;
                }
                else
                {
                    auto k1 = nums[i - 1] - nums[i] + j;
                    auto k2 = j;
                    if (min(k1, k2) < 0)
                    {
                        d[i][j] = 0;
                    }
                    else
                    {
                        d[i][j] = s[i - 1][min(k1, k2)];
                    }

                }

                if (j < 0)
                {
                    s[i][j] = 0;
                }
                else if (j == 0)
                {
                    s[i][j] = d[i][j] % MOD;
                }
                else
                {
                    s[i][j] = (s[i][j - 1] + d[i][j]) % MOD;
                }
            }
        }


        auto ans = s[n - 1][nums[n - 1] ];
        return ans;
    }
};
